mapper.select:
SELECT G.*, U.*, I.*, P.*, COUNT(C.G_NO) AS GC_CNT FROM T4_GBOARD G INNER JOIN T4_USER U ON G.U_ID = U.U_ID LEFT JOIN T4_GBOARD_IMG I ON G.G_NO = I.G_NO LEFT JOIN T4_GCOMMENTS C ON G.G_NO = C.G_NO LEFT JOIN T4_GBOARD_PROFILE P ON U.U_ID = P.U_ID AND G.ARTIST = P.ARTIST WHERE G.ARTIST = #{artist} AND (GC_DELYN = 'N' OR GC_DELYN IS NULL)
AND G.G_NO = #{gNo}
GROUP BY G.G_NO ORDER BY G_DATE DESC

SELECT G.*, U.*, I.*, P.*, (SELECT COUNT(*) FROM T4_GCOMMENTS C WHERE C.G_NO = G.G_NO) AS GC_CNT FROM T4_GBOARD G INNER JOIN T4_USER U ON G.U_ID = U.U_ID LEFT JOIN T4_GBOARD_IMG I ON G.G_NO = I.G_NO LEFT JOIN T4_GBOARD_PROFILE P ON U.U_ID = P.U_ID AND G.ARTIST = P.ARTIST WHERE G.ARTIST = #{artist} AND G_ARTIST = 'Y'
AND G.G_NO = #{gNo}
ORDER BY G_DATE DESC

mapper.update:
UPDATE T4_GBOARD SET 	
	G_DELYN = 'Y'
WHERE G_NO = #{gNo}

UPDATE T4_GBOARD SET
G_LIKE = G_LIKE + 1
WHERE ARTIST = #{artist} AND G_NO = #{gNo}

UPDATE T4_GBOARD SET
G_LIKE = G_LIKE - 1
WHERE ARTIST = #{artist} AND G_NO = #{gNo}

mapper.select:
SELECT COUNT(*)
FROM T4_GBOARD_LIKE
WHERE G_NO = #{gNo} AND U_ID = #{uId}

mapper.update:
UPDATE T4_GCOMMENTS SET 
	GC_DELYN = 'Y'
	WHERE GC_NO = #{gcNo}

UPDATE T4_GCOMMENTS SET
GC_LIKE = GC_LIKE + 1
WHERE ARTIST = #{artist} AND GC_NO = #{gcNo}

UPDATE T4_GCOMMENTS SET
GC_LIKE = GC_LIKE - 1
WHERE ARTIST = #{artist} AND GC_NO = #{gcNo}

mapper.select:
SELECT COUNT(*)
FROM T4_GCOMMENTS_LIKE
WHERE GC_NO = #{gcNo} AND U_ID = #{uId}

mapper.update:
UPDATE T4_GCOMMENTS SET
GC_DELYN = 'Y'
WHERE GC_NO = #{gcNo}

UPDATE T4_GBOARD
SET G_BANYN = G_BANYN + 1
WHERE G_NO = #{gNo}

UPDATE T4_GCOMMENTS
SET GC_BANYN = GC_BANYN + 1
WHERE GC_NO = #{gcNo}

UPDATE T4_GBOARD_PROFILE
SET NICKNAME = #{nickName}
WHERE GP_NO = #{gpNo}

UPDATE T4_GBOARD_PROFILE
SET GP_FILENAME = #{filename}, GP_PATH = #{path}
WHERE GP_NO = #{gpNo}

UPDATE T4_GBOARD_PROFILE
SET GP_FILENAME = NULL, GP_PATH = NULL
WHERE GP_NO = #{gpNo}
