mapper.select:
SELECT * FROM T4_PRODUCT P
LEFT JOIN T4_PRODUCT_IMG I ON P.P_NO = I.P_NO
WHERE 1=1
    AND ARTIST = #{artist}

    AND CATEGORY = #{ctg}



        ORDER BY P_NAME ASC


        ORDER BY PRICE ASC


        ORDER BY PRICE DESC





LIMIT #{startNum}, #{lastNum}

